mapper.select:
select
count(*) as total
u.uid,u.mobile,u.nick,quota.realname,u.gender,quota.auth_status,extend.invite_code,u.created_at as createdAt
from users u left join users_quota quota on quota.uid = u.uid left join users_extend extend on extend.uid = quota.uid
where 1=1
and mobile like CONCAT('%',#{phone},'%')
and u.nick like CONCAT('%',#{username},'%')
and quota.realname like CONCAT('%',#{realname},'%')
and quota.auth_status = #{status}
and extend.invite_code = #{inviteid}
and u.created_at > #{fromtime} and u.created_at < #{totime}
LIMIT #{start},#{size}

select quota.uid , u.nick , u.mobile , quota.auth_status , quota.realname , u.gender, quota.contact , quota.id_card , quota.origin , quota.school_location , quota.idcard_reverse , quota.idcard_front
from users u ,users_quota quota
where quota.uid = u.uid and quota.uid = #{uid}
