mapper.select:
select , city.name as cityName from hospital,area_city city where city.id = hospital.city_id
and hospital.name like CONCAT('%',#{name},'%')
and hospital.city_id = #{cityid}

mapper.update:
update hospital set name=#{name}, img=#{img}, 
address=#{address}, updated_at =now(),city_id = #{cityId}
 where id=#{id}
